dict.string:
ObjC.import('stdlib');

function setVariable(key, val, exportable) {
	Application('com.runningwithcrayons.Alfred').setConfiguration(key, {
		inWorkflow: $.getenv('alfred_workflow_bundleid'),
		toValue: val,
		exportable: exportable,
	});
}


function removeVariable(key) {
	Application('com.runningwithcrayons.Alfred').removeConfiguration(key, {
  		inWorkflow: $.getenv('alfred_workflow_bundleid')
	});
}


function run(argv) {
	var key = $.getenv('configure_key');
	var value = $.getenv('configure_val');
	var exportable = $.getenv('configure_exportable') === 'true';
	var deleteVar = false;

	try {
	    deleteVar = $.getenv('configure_remove') === 'true';
	} catch (e) {
    		deleteVar = false;
	}

	if (deleteVar) {
		removeVariable(key);
	} else {
		setVariable(key, value, exportable);
	}

}
